	/**	 * insert into  keke_witkey_feed  ,or add new record	 * @return int last_insert_id	 */	function create_keke_witkey_feed(){		$data =  array();		if(!is_null($this->_feed_id)){			$data['feed_id']=$this->_feed_id;		}		if(!is_null($this->_obj_id)){			$data['obj_id']=$this->_obj_id;		}		if(!is_null($this->_obj_link)){			$data['obj_link']=$this->_obj_link;		}		if(!is_null($this->_feedtype)){			$data['feedtype']=$this->_feedtype;		}		if(!is_null($this->_uid)){			$data['uid']=$this->_uid;		}		if(!is_null($this->_username)){			$data['username']=$this->_username;		}		if(!is_null($this->_icon)){			$data['icon']=$this->_icon;		}		if(!is_null($this->_title)){			$data['title']=$this->_title;		}		if(!is_null($this->_feed_desc)){			$data['feed_desc']=$this->_feed_desc;		}		if(!is_null($this->_feed_pic)){			$data['feed_pic']=$this->_feed_pic;		}		if(!is_null($this->_feed_time)){			$data['feed_time']=$this->_feed_time;		}		if(!is_null($this->_ext_data)){			$data['ext_data']=$this->_ext_data;		}		return $this->_feed_id = $this->_db->inserttable($this->_tablename,$data,1,$this->_replace);	}
	/**	 * edit table keke_witkey_feed	 * @return int affected_rows	 */	function edit_keke_witkey_feed(){		$data =  array();		if(!is_null($this->_feed_id)){			$data['feed_id']=$this->_feed_id;		}		if(!is_null($this->_obj_id)){			$data['obj_id']=$this->_obj_id;		}		if(!is_null($this->_obj_link)){			$data['obj_link']=$this->_obj_link;		}		if(!is_null($this->_feedtype)){			$data['feedtype']=$this->_feedtype;		}		if(!is_null($this->_uid)){			$data['uid']=$this->_uid;		}		if(!is_null($this->_username)){			$data['username']=$this->_username;		}		if(!is_null($this->_icon)){			$data['icon']=$this->_icon;		}		if(!is_null($this->_title)){			$data['title']=$this->_title;		}		if(!is_null($this->_feed_desc)){			$data['feed_desc']=$this->_feed_desc;		}		if(!is_null($this->_feed_pic)){			$data['feed_pic']=$this->_feed_pic;		}		if(!is_null($this->_feed_time)){			$data['feed_time']=$this->_feed_time;		}		if(!is_null($this->_ext_data)){			$data['ext_data']=$this->_ext_data;		}		if($this->_where){			return $this->_db->updatetable($this->_tablename,$data,$this->getWhere());		}		else{			$where = array('feed_id' => $this->_feed_id);			return $this->_db->updatetable($this->_tablename,$data,$where);		}	}
	/**	 * query table: keke_witkey_feed,if isset where return where record,else return all record	 * @return array	 */	function query_keke_witkey_feed($is_cache=0, $cache_time=0){		if($this->_where){			$sql = "select * from $this->_tablename where ".$this->_where;		}		else{			$sql = "select * from $this->_tablename";		}		if ($is_cache) {			$this->_cache_config ['is_cache'] = $is_cache;		}		if ($cache_time) {			$this->_cache_config ['time'] = $cache_time;		}		if ($this->_cache_config ['is_cache']) {			if (CACHE_TYPE) {				$keke_cache = new keke_cache_class ( CACHE_TYPE );				$id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');				$data = $keke_cache->get ( $id );				if ($data) {					return $data;				} else {					$res = $this->_dbop->query ( $sql );					$keke_cache->set ( $id, $res,$this->_cache_config['time'] );					$this->_where = "";					return $res;				}			}		}else{			$this->_where = "";			return  $this->_dbop->query ( $sql );		}	}
	/**	 * query count keke_witkey_feed records,if iset where query by where	 * @return int count records	 */	function count_keke_witkey_feed(){		if($this->_where){			$sql = "select count(*) as count from $this->_tablename where ".$this->_where;		}		else{			$sql = "select count(*) as count from $this->_tablename";		}		$this->_where = "";		return $this->_dbop->getCount($sql);	}
	/**	 * delete table keke_witkey_feed, if isset where delete by where	 * @return int deleted affected_rows	 */	function del_keke_witkey_feed(){		if($this->_where){			$sql = "delete from $this->_tablename where ".$this->_where;		}		else{			$sql = "delete from $this->_tablename where feed_id = $this->_feed_id ";		}		$this->_where = "";		return $this->_dbop->execute($sql);	}

	 
	 
