	/**	 * insert into  keke_witkey_model  ,or add new record	 * @return int last_insert_id	 */	function create_keke_witkey_model(){		$data =  array();		if(!is_null($this->_model_id)){			$data['model_id']=$this->_model_id;		}		if(!is_null($this->_model_code)){			$data['model_code']=$this->_model_code;		}		if(!is_null($this->_model_name)){			$data['model_name']=$this->_model_name;		}		if(!is_null($this->_model_dir)){			$data['model_dir']=$this->_model_dir;		}		if(!is_null($this->_model_type)){			$data['model_type']=$this->_model_type;		}		if(!is_null($this->_model_dev)){			$data['model_dev']=$this->_model_dev;		}		if(!is_null($this->_model_status)){			$data['model_status']=$this->_model_status;		}		if(!is_null($this->_model_desc)){			$data['model_desc']=$this->_model_desc;		}		if(!is_null($this->_on_time)){			$data['on_time']=$this->_on_time;		}		if(!is_null($this->_hide_mode)){			$data['hide_mode']=$this->_hide_mode;		}		if(!is_null($this->_listorder)){			$data['listorder']=$this->_listorder;		}		if(!is_null($this->_model_intro)){			$data['model_intro']=$this->_model_intro;		}		if(!is_null($this->_indus_bid)){			$data['indus_bid']=$this->_indus_bid;		}		if(!is_null($this->_config)){			$data['config']=$this->_config;		}		return $this->_model_id = $this->_db->inserttable($this->_tablename,$data,1,$this->_replace);	}
	/**	 * edit table keke_witkey_model	 * @return int affected_rows	 */	function edit_keke_witkey_model(){		$data =  array();		if(!is_null($this->_model_id)){			$data['model_id']=$this->_model_id;		}		if(!is_null($this->_model_code)){			$data['model_code']=$this->_model_code;		}		if(!is_null($this->_model_name)){			$data['model_name']=$this->_model_name;		}		if(!is_null($this->_model_dir)){			$data['model_dir']=$this->_model_dir;		}		if(!is_null($this->_model_type)){			$data['model_type']=$this->_model_type;		}		if(!is_null($this->_model_dev)){			$data['model_dev']=$this->_model_dev;		}		if(!is_null($this->_model_status)){			$data['model_status']=$this->_model_status;		}		if(!is_null($this->_model_desc)){			$data['model_desc']=$this->_model_desc;		}		if(!is_null($this->_on_time)){			$data['on_time']=$this->_on_time;		}		if(!is_null($this->_hide_mode)){			$data['hide_mode']=$this->_hide_mode;		}		if(!is_null($this->_listorder)){			$data['listorder']=$this->_listorder;		}		if(!is_null($this->_model_intro)){			$data['model_intro']=$this->_model_intro;		}		if(!is_null($this->_indus_bid)){			$data['indus_bid']=$this->_indus_bid;		}		if(!is_null($this->_config)){			$data['config']=$this->_config;		}		if($this->_where){			return $this->_db->updatetable($this->_tablename,$data,$this->getWhere());		}		else{			$where = array('model_id' => $this->_model_id);			return $this->_db->updatetable($this->_tablename,$data,$where);		}	}
	/**	 * query table: keke_witkey_model,if isset where return where record,else return all record	 * @return array	 */	function query_keke_witkey_model($is_cache=0, $cache_time=0){		if($this->_where){			$sql = "select * from $this->_tablename where ".$this->_where;		}		else{			$sql = "select * from $this->_tablename";		}		if ($is_cache) {			$this->_cache_config ['is_cache'] = $is_cache;		}		if ($cache_time) {			$this->_cache_config ['time'] = $cache_time;		}		if ($this->_cache_config ['is_cache']) {			if (CACHE_TYPE) {				$keke_cache = new keke_cache_class ( CACHE_TYPE );				$id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');				$data = $keke_cache->get ( $id );				if ($data) {					return $data;				} else {					$res = $this->_dbop->query ( $sql );					$keke_cache->set ( $id, $res,$this->_cache_config['time'] );					$this->_where = "";					return $res;				}			}		}else{			$this->_where = "";			return  $this->_dbop->query ( $sql );		}	}
	/**	 * query count keke_witkey_model records,if iset where query by where	 * @return int count records	 */	function count_keke_witkey_model(){		if($this->_where){			$sql = "select count(*) as count from $this->_tablename where ".$this->_where;		}		else{			$sql = "select count(*) as count from $this->_tablename";		}		$this->_where = "";		return $this->_dbop->getCount($sql);	}
	/**	 * delete table keke_witkey_model, if isset where delete by where	 * @return int deleted affected_rows	 */	function del_keke_witkey_model(){		if($this->_where){			$sql = "delete from $this->_tablename where ".$this->_where;		}		else{			$sql = "delete from $this->_tablename where model_id = $this->_model_id ";		}		$this->_where = "";		return $this->_dbop->execute($sql);	}



