INSERT INTO bao_activity (activity_id, cate_id, shop_id, tuan_id, city_id, area_id, business_id, title, intro, photo, thumb, details, price, bg_date, end_date, `time`, sign_end, addr, orderby, audit, closed, sign_num, create_time, create_ip, template) VALUES
(1, 19, 2, 0, 1, 1, 2, '徽州自由行红酒烛光晚宴', '本次活动游戏增加骑大马，朋友们，记得不要穿西服哦，容易炸线。漏了，你就躲起来吧！', '2015/04/23/thumb_5538bc0cccfb5.jpg', NULL, '<p><span style="color: rgb(0, 0, 0); font-family: &#39;Microsoft Yahei&#39;, Helvetica, Arial; line-height: 21px; white-space: normal; widows: auto; float: none;">【徽州自由行活动公告】徽州自由行户外俱乐部2015年4月25日 西餐の“红酒烛光晚宴”活动（正式版） 本次活动内容：1）徒步湿地公园2）半岛公园搭建帐篷 布置场地3）烤羊排或者猪排4）点燃蜡烛 放飞梦想所需物大类：羊排或者猪排 红酒 帐篷 音响 烟花等活动目的: 增进友谊 为二日自助游作准备，找出问题解决问题。为下一次的户外超长徒步建立更好的后勤保障。 1）徒步计划合肥明珠广场集合13：:30分，准确地点：明珠广场会展中心北门停车场，如遇特殊环境，路边停靠。请有私家车的车主朋友们，顺带一下网友，谢谢！14：00 准时出发。 14:30分到达滨湖湿地公园，大门口进行合影拍照。准备物品：相机 徽州自由行队旗 摄像人员14:25分准时进行徒步活动，本次徒步预计滨湖湿地公园内进行15公里 如果遇到儿童，将适当缩短距离。17:30分结束，至巢湖岸边，爱拍的拍拍照，不爱拍的看看湖。18:00准时出发至肥东半岛公园 由于半岛公园相对隐蔽，请各位私家车主勿要擅自掉队，不然很难准时集合。18:20分到达半岛公园。半岛公园中心区，搭建帐篷(自行购买，建议价格50元内不含防潮垫，也可由自由行进行集体采购，需要集体采购请提前三天联系）防潮垫 烤炉 桌子等 规划面积100平方。露营值班：圈圈 -早上出发（值班负责：队员的人身安全（随身携带电击棍，非意外情况，不得使用），财产请勿携带，请保管好自己的手机以及项链等贵重物品，禁止混账，如果发现，全员群殴） 搭建烤炉，拿出羊排或者猪排，点燃红色蜡烛（本场活动照明全程蜡烛）红酒摇曳，激情的碰撞，我们动起来吧，游戏中找回儿时的乐趣，本次活动游戏增加骑大马，朋友们，记得不要穿西服哦，容易炸线。漏了，你就躲起来吧！羊排猪排烧烤时间较长，尤其圈主为大家带来悉心研究成果的韩式烤羊排更是需要耐心，所以大家忍住，尽情的玩游戏去吧。圈主的秘方不能泄露，纯手工的哦 00:00分 百花齐放，大声喊出你的梦想，请每个人在烟花璀璨的那一刻对着茫茫巢湖，喊出她的名字。（但是也不能混账，我们都是守礼节的人）00:20分 入账，各自入睡至天明（为了保证队员的生命安全，驾驶员同志请合理饮酒。） 一：活动报名方式： 1）加徽州自由行户外俱乐部群93130227私聊圈圈报名缴费 2）支付宝账号： 14790484956（刘兴强）注意事项：由于本次活动涉及到前期购买食材，需要预付50%保证金，如因为天气原因活动未能如期举办保证金如数退还，缴纳保证金后，放鸽子费用一律不退。 二：费用核算：暂定98每人（多退少补，多余部分以充话费形式返还） 自驾注意事项：1） 由于本次活动属于自发类户外活动，交通工具属网友自发提供，请各位驾驶员同志在驾驶车辆过程中，安全驾驶。请勿超速 超载。副驾驶请按照要求佩戴安全带。2）车辆行驶过程中，倡导绿色出行，队员禁止向车窗外抛洒垃圾以及塑料袋3）本次活动计划用时半天一夜。请合理安排自己的时间。&nbsp;</span><br style="margin: 0px; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, Helvetica, Arial; font-size: 14px; line-height: 21px; white-space: normal; widows: auto;"/><img src="http://ugc.qpic.cn/qqac/G7JJEJiciaqwkaFE5Z8fqTFqzrViaF21EBSA0774wMNKHY/auto" id="_img08423107566777617" data-w="800" data-h="600" width="598" height="448" style="margin: 0px; padding: 0px; font-family: &#39;Microsoft Yahei&#39;, Helvetica, Arial; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; display: block;"/></p>', 240, '2014-10-09', '2018-11-29', '2015-03-26 7:00 -- 18：00', '2015-02-12', '合肥 滨湖湿地公园', 1, 1, 0, 1, NULL, NULL, NULL),
(3, 9, 60, 0, 1, 1, 2, '2015第二届中国（上海）食品安全科技峰会', '首届中国（上海）食品安全科技峰会于2014年11月20-21日成功举办，大会针对食品安全移动快速检测、法规与标准、质量控制和可追溯体系建设等相关议题进行分享与交流，同时也就在移动互联时代，大数据、物联网、云计算等高新科技如何保障食品安全进行深入的探讨，在各', '2015/04/23/thumb_5538c7d8b4036.jpg', NULL, '<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">首届<span style="box-sizing: border-box; font-weight: 700;">中国（上海）食品安全科技峰会</span>于2014年11月20-21日成功举办，大会针对食品安全移动快速检测、法规与标准、质量控制和可追溯体系建设等相关议题进行分享与交流，同时也就在移动互联时代，大数据、物联网、云计算等高新科技如何保障食品安全进行深入的探讨，在各方的共同努力下，取得了丰硕的成果。</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;"><img alt="食品安全" src="http://pic.huodongjia.com/event/2015-03-01/event1425175045.06.jpg" class="img-responsive" style="box-sizing: border-box; border-width: 0px; vertical-align: middle; display: block; max-width: 100%; height: 200px; margin: 0px auto; line-height: 1.6em; width: 1000px;"/></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal; text-align: center;">(2015第二届中国（上海）食品安全科技峰会)</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">“<span style="box-sizing: border-box; font-weight: 700;">2015第二届中国（上海）食品安全科技峰会</span>”拟定于2015年5月21日-22日在上海举办。由上海市食品学会、上海市食品安全协会联合会主办，上海海洋大学、上海浩韵文化传播有限公司承办，大会将邀请联合国粮食及农业组织、中国食品科技学会、上海市食品药品监督管理局、中国检验检疫科学研究院等相关政府监管机构、行业协会、重点企业和权威专家的大力支持。论坛从食品安全技术创新、行业标准、产业链合作等方面进行充分交流与沟通，探讨高科技如何助力食品安全！</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">【<span style="box-sizing: border-box; font-weight: 700;">会议内容</span>】</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;"><span style="box-sizing: border-box; color: rgb(0, 100, 0);">1、食品分析与安全检测新技术 -- &nbsp; &nbsp;</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">食品分析检测新技术、新产品，实验室分析仪器装备，食品安全快速检测技术及应用，新一代移动检测车在食品安全中的应用；</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;"><span style="box-sizing: border-box; color: rgb(0, 100, 0);">2、海峡两岸共筑食品安全合作交流 --</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">沪台食品安全保障法规及政策，食品安全标准制定及产业发展，食品安全趋势与对策；</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;"><span style="box-sizing: border-box; color: rgb(0, 100, 0);">3、食品质量安全信息追溯技术 -- &nbsp;</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">食品追溯体系建设的政策与标准化发展，食品安全追溯系统电子监管平台建设，物联网、云计算等新技术在追溯体系建设中的应用，食品供应链质量管控与企业追溯系统解决方案；</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;"><span style="box-sizing: border-box; color: rgb(0, 100, 0);">4、HACCP应用与食品安全法律保障 --</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 16px; line-height: 30px; white-space: normal;">HACCP在我国食品安全管理领域的推广和应用，食品生产过程质量安全分险防范与监控，食品企业法律责任、网购、电商食品安全法律保障；</p><p><br/></p>', 150, '2014-10-24', '2018-11-29', '2015-03-26 7:20 -- 18：00', '2015-02-12', '上海浩韵文化传播有限公司', 1, 1, 0, 5, 1414462723, '127.0.0.1', NULL),
(4, 8, 32, 0, 1, 1, 2, '重庆全地形车ATV穿越体验', '重庆伍合多吉越野俱乐部将周边休闲与户外活动合二为一，以ATV/UTV（全地形车）穿越为主要运营项目，打造时尚、健康的新型周边休闲。俱乐部殷勤接待每一位玩家，竭诚为玩家提供贴心服务，为玩家开启一段放松、有趣、欢愉的闲暇时光之旅。', '2015/04/23/thumb_5538c8a6eecd2.jpg', NULL, '<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; word-break: break-word; line-height: 1.9em; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 14px; white-space: normal;"><span style="box-sizing: border-box; font-weight: 700;">重庆</span>伍合多吉越野俱乐部将周边休闲与户外活动合二为一，以<span style="box-sizing: border-box; font-weight: 700;">ATV/UTV（全地形车）</span>穿越为主要运营项目，打造时尚、健康的新型周边休闲。俱乐部殷勤接待每一位玩家，竭诚为玩家提供贴心服务，为玩家开启一段放松、有趣、欢愉的闲暇时光之旅。</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; word-break: break-word; line-height: 1.9em; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 14px; white-space: normal; text-align: center;"><img class="img-responsive" src="http://pic.huodongjia.com/img/Travel/2014-06/212864_3.jpg" style="box-sizing: border-box; border-width: 0px; vertical-align: middle; display: block; max-width: 100%; height: 369px; margin: 1em auto; width: 550px;"/></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; word-break: break-word; line-height: 1.9em; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 14px; white-space: normal;">当你与家人朋友想暂别城市中心，共度一个特别的假日，就赶快打电话告诉我们的团队吧！一起来拥抱自然，呼吸新鲜空气，挑战刺激的户外活动，不但可以驾驶ATV，还可以体验<span style="box-sizing: border-box; font-weight: 700;">实弹射击</span>的乐趣，在活动中释放压力，解放身心，发现不一样的自己。</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; word-break: break-word; line-height: 1.9em; color: rgb(44, 62, 80); font-family: Tahoma, Arial, Helvetica, &#39;Microsoft Yahei&#39;, 微软雅黑, SimHei; font-size: 14px; white-space: normal; text-align: center;"><img class="img-responsive" src="http://pic.huodo