INSERT INTO bao_billboard (list_id, title, intro, photo, cate_id, looknum, orderby, closed, create_time, create_ip, is_hot, is_new, is_chose) VALUES
(7, '开春减肥的去处 上海好评不断的专业瑜伽会所', '春天都已经来了，夏天也不远了。穿短裙的季节，再不减肥就晚了。瑜伽不仅能够健康减肥，更能够舒缓城市人的压力，还考虑什么呢？找间好评如潮的瑜伽会所，一起减肥吧！', '2015/04/23/thumb_5538aeeb46650.jpg', 7, 14, 5, 0, 1428490473, '127.0.0.1', 1, 1, 1),
(6, '上海天大医疗美容医院', '上海天大医疗美容医院是上海乃至国内规模最大、设备一流、技术雄厚的中外整形美容专科医院之一。天大医疗美容医院目前与英国、韩国、德国、以色列等国家成立了中外整形医学联盟,并专门设立了中外整形失败援助.天大医疗美容医院拥有包括心理整形和外貌整形在内的4位专业博士', '2015/04/23/thumb_5538ad7da36ef.jpg', 7, 221, 7, 0, 1428482340, '127.0.0.1', 1, 1, 1),
(9, '魔都高端大气上档次SPA推荐 享受极致特色理疗', '每天都踩着鼓点过日子，在上海这样一个节奏如此快的城市，有谁会不爱SPA呢？一次放松的SPA已经成为一种必须，而不是愿望。上海哪里能给你最极致的SPA体验，那就看美位为你罗列的SPA地图吧！', '2015/04/23/thumb_5538ae43d169f.jpg', 7, 10, 3, 0, 1428482340, '127.0.0.1', 1, 1, 1),
(10, '昭君时尚纹绣连锁(静安店)', '昭君.时尚纹饰艺术传媒（以下称昭君纹绣），始创于1989年，秉承诚信、沟通、责任、专业、专注的核心价值观，稳步发展，至今已为近2万名爱美女性量身打造美丽容颜，我们的使命是：专业纹绣，缔造完美，让天下女人不化妆都没有缺憾。 昭君纹绣在山东发展20年，为了满足', '2015/04/23/thumb_5538ae1353010.jpg', 7, 10, 6, 0, 1428908759, '127.0.0.1', 1, 0, 1),
(12, '上海美联臣医疗美容医院', '上海美联臣医疗美容医院最好的美容机构', '2015/04/14/thumb_552c90ade390c.png', 7, 7, 8, 0, 1428983986, '127.0.0.1', 1, 1, 1),
(13, '梵医馆(印度精油按摩皇家会所)', '印度风情休闲养生。养脑是养生的最高境界，定位于服务脑力工作者的养脑俱乐部！都市白领心灵休憩的港湾！相彰于瑜伽术的养生与驻颜的经典！ 风靡世界的印度养生法正迅速抢滩上海！阿育吠陀作为印度最早的养生医学论述专著，和最古老的养生方法，六千多年来，在世界无数家庭中', '2015/04/23/thumb_5538ade43ed0b.jpg', 7, 9, 2, 0, 1428984033, '127.0.0.1', 1, 1, 1),
(15, 'LaMoon婚纱礼服(淮海店)', 'LaMoon婚纱礼服(淮海店)', '2015/04/14/thumb_552c9b5146483.png', 7, 14, 1, 0, 1428986706, '127.0.0.1', 1, 1, 1),
(20, '打着灯笼都难找的便宜小饭馆', '现在的饭店均价基本都已飙升100，对于上班族来说简直就是坑爹啊！平价的小店，真是人见人爱~不仅节约开支，更能吃到饱！！', '2015/04/16/thumb_552f3f9129db2.png', 1, 4, 10, 0, 1429159826, '127.0.0.1', 1, 1, 1),
(17, '静安区十大小资咖啡馆推荐', '静安区，全上海唯一一个所有区境都位于内环内的市中心黄金城区，闹中取静，环境优美，好地段好的咖啡馆自然不少，小编为你推荐十家有特色的咖啡！', '2015/04/23/thumb_5538acd20601e.jpg', 10, 8, 12, 0, 1428986829, '127.0.0.1', 1, 1, 1),
(21, '美食|上海超人气海鲜自助餐厅推荐', '自助餐厅很多，有火锅自助、烤肉自助、海鲜自助等等，当然价格也不一样，自己动手，选择自己喜欢...', '2015/04/16/thumb_552f3fd3cd23f.png', 1, 1, 11, 0, 1429159925, '127.0.0.1', 1, 1, 1),
(22, '魔都超日系的美食小铺', '魔都人民讲究吃，魔都好吃的自然也不少！可是要从茫茫店铺中脱颖而出也真是不容易~今天就给大家...', '2015/04/16/thumb_552f403d1f12d.png', 1, 1, 12, 0, 1429160017, '127.0.0.1', 1, 1, 1),
(23, '魔都吃帝王蟹的好地方', '我知道你喜欢吃大闸蟹，可是除了大闸蟹，其实还有很多横行的家伙也很美味，比如今天为你介绍的帝...', '2015/04/16/thumb_552f408465e4b.png', 1, 9, 14, 0, 1429160068, '127.0.0.1', 1, 1, 1),
(24, '上海办草坪婚礼必考虑的婚宴酒店', '现在草坪婚礼是越来越流行了，那又有哪些酒店可以办草坪婚礼的呢？又有哪些婚宴酒店办草坪婚礼经...', '2015/04/16/thumb_552f40edb4c1b.png', 5, 1, 10, 0, 1429160191, '127.0.0.1', 1, 1, 1),
(25, '再胖都显瘦的上海婚纱礼服店推荐', '一辈子一次的结婚大事，总要各个方面都做足了面子，婚车也是很重要的。虽然工薪阶层买不起什么豪..', '2015/04/25/thumb_553b145b9bd30.jpg', 5, 1, 11, 0, 1429160256, '127.0.0.1', 1, 1, 1),
(26, '上海超梦幻婚礼会所大比拼', '随着时代的变迁，传统的婚礼场地和服务已经越来越难适应现下年轻人的结婚需求了，为了满足这一需..', '2015/04/25/thumb_553b156c7e7ab.jpg', 5, 7, 14, 0, 1429160315, '127.0.0.1', 1, 1, 1),
(27, '上海值得信赖的宠物医院推荐', '生活便民|上海值得信赖的宠物医院推荐', '2015/04/16/thumb_552f42a8993ff.png', 6, 3, 12, 0, 1429160617, '127.0.0.1', 1, 1, 1),
(28, '上海秋季适合拍婚纱照的地方推荐', '生活便民|秋风起落叶飞 上海秋季适合拍婚纱照的地方推荐', '2015/04/16/thumb_552f42ec99f48.png', 6, 5, 13, 0, 1429160689, '127.0.0.1', 1, 1, 1),
(29, '上海靠谱的钟点小时工大盘点', '生活便民|上海靠谱的钟点小时工大盘点', '2015/04/16/thumb_552f432b23330.png', 6, 3, 15, 0, 1429160748, '127.0.0.1', 1, 1, 1),
(30, '上海豪华专业月子会所推荐', '婴儿用品是婴儿出生必不可少的东西，比如说衣服、床、推车、玩具等等，宝宝是爸爸妈妈们的心肝宝..', '2015/04/16/thumb_552f43c46359e.png', 8, 1, 1, 0, 1429160901, '127.0.0.1', 1, 1, 1),
(31, '沪上小孩子喜欢去玩的地方', '欢乐谷、锦江乐园可能已经带你的孩子去了个遍… 今天在这和大家说说我收集的一些既可以玩，又能...', '2015/04/16/thumb_552f440143f97.png', 8, 5, 2, 0, 1429160962, '127.0.0.1', 1, 1, 1),
(32, '|沪上高性价比幼儿SPA游泳馆', '如今，婴儿游泳已经是宝宝出生后父母都会参与体验的亲子活动，看着宝宝在水里扑腾，爸妈都喜笑颜.', '2015/04/16/thumb_552f44694e75e.png', 8, 0, 3, 0, 1429161069, '127.0.0.1', 1, 1, 1),
(33, '虹口区值得推荐的幼儿园排名', '今天我们来盘点一下魔都那些有着百年历史的中学，可能她们现在不能比肩“四大名校”，但是经历了...', '2015/04/16/thumb_552f459b3436e.png', 9, 1, 16, 0, 1429161375, '127.0.0.1', 1, 1, 1),
(34, '教育培训|黄浦区值得推荐的幼儿园排名', '你有在上海黄浦区找到过非常不错的幼儿园吗？今天，我就找到了这么几家非常不错的，看看有你去过...', '2015/04/16/thumb_552f460ed158d.png', 9, 3, 17, 0, 1429161488, '127.0.0.1', 1, 1, 1),
(35, '闸北区值得推荐的幼儿园排名', '你有在上海闸北区找到过非常不错的幼儿园吗？今天，我就找到了这么几家非常不错的，看看有你去过...', '2015/04/25/thumb_553b23f84fa7f.jpg', 9, 0, 100, 0, 1429161646, '127.0.0.1', 1, 1, 1),
(36, '魔都历史悠久的中学大盘点 有你的母校吗？', '今天我们来盘点一下魔都那些有着百年历史的中学，可能她们现在不能比肩“四大名校”，但是经历了...', '2015/04/25/thumb_553b2347ae144.jpg', 9, 1, 100, 0, 1429161781, '127.0.0.1', 1, 1, 1),
(37, '盘点上海人气的驻唱酒吧', '当年从《星光》出来的萧敬腾、林宥嘉、徐佳莹、胡夏，如今已经是华语乐坛的生力军，各大电视频道...', '2015/04/16/thumb_552f47ba1f3b3.png', 10, 10, 10, 0, 1429161915, '127.0.0.1', 1, 1, 1),
(38, '上海受欢迎的电影院排名', '上海好的电影院是哪家？随着生活质量的提高，去电影院看电影已经成为比较流行的休闲生活方式，如...', '2015/04/16/thumb_552f47ed71bc4.png', 10, 1, 12, 0, 1429161968, '127.0.0.1', 1, 1, 1),
(39, '上海周边十大人气农家乐推荐', '农家乐是新兴的旅游休闲形式，是农民向城市现代人提供的一种回归自然从而获得身心放松、愉悦精神...', '2015/04/16/thumb_552f4a803366a.png', 11, 3, 12, 0, 1429162625, '127.0.0.1', 1, 1, 1),
(40, '上海值得推荐的度假村排行', '有人度假旅游，不为风景，纯粹只为了换一个舒适的环境减减压力。度假村就是个不错的选择，摆脱城...', '2015/04/16/thumb_552f4aabd69bb.png', 11, 1, 100, 0, 1429162669, '127.0.0.1', 1, 1, 1),
(41, '上海风情浓郁的酒店推荐', '如果已经厌倦了快捷酒店长的一模一样的房间，在上海可以住极具老上海特色的酒店，寻找渐渐消失的...', '2015/04/16/thumb_552f4ad928312.png', 11, 1, 100, 0, 1429162713, '127.0.0.1', 1, 1, 1),
(42, '上海值得推荐的精品酒店', '当国内其他地方都仍在用五星级酒店作为城市新名片时，上海的酒店业在朝一个更深的方向蜕变。精品...', '2015/04/16/thumb_552f4b2e57092.png', 11, 0, 100, 0, 1429162800, '127.0.0.1', 1, 1, 1),
(43, '网罗沪上有亲子设施的星级酒店', '暑期带孩子来上海旅游吧！上海的星级酒店不少，有哪几家是有亲子设施的呢？带孩子出来玩，当然要...', '2015/04/16/thumb_552f4b8766af0.png', 11, 3, 100, 0, 1429162888, '127.0.0.1', 1, 1, 1),
(44, '年会之选 盘点2014上海滩特色洋房西餐厅', '新年的脚步越来越近，到处都洋溢着节日的气氛。在这辞旧迎新之际，年会大餐是必不可少的。下面就是我们为您精选的一些别具特色的洋房年会餐厅。', '2015/04/23/thumb_5538ab811dde0.jpg', 1, 3, 100, 0, 1429162998, '127.0.0.1', 1, 1, 1),
(45, '日月光美食广场里的小吃推荐', '有人说，日月光是个烧钱吃东西的地方，其实不然，这是一个很神奇的地方，只要做足功课、选择得当，仅仅需要花一点点钱，就能吃到各种美食，而且甜品小食、奶茶饮料、正餐、中外美食都有噢。小编推荐性价比超高的日月光美食！', '2015/04/23/thumb_5538ab3cd01cf.jpg', 1, 7, 100, 0, 1429163045, '127.0.0.1', 1, 1, 1),
(46, '燃烧的美味 上海超炫酷火焰美食大盘点', '香气四溢的火焰虾、高端大气的火焰牛排、冰火交融的火焰冰激凌……美食和火焰的碰撞，有木有让你的味蕾和心情燃烧起来呢？下面就来一一盘点炫酷的“火焰美食”！', '2015/04/23/thumb_5538aaff95271.jpg', 1, 11, 23, 0, 1429164138, '127.0.0.1', 1, 1, 0),
(47, '软糯一口一个香 沪上超人气芋头美食小吃', '芋头真的是个很难打理的食材，想要在家好好地炖烂它，需要的就是你的时间和耐心了。与其费劲巴拉地自己做，还不如找一家吃芋头的良店，无论是甜点还是热菜，全都上上来！', '2015/04/23/thumb_5538aab4c472a.jpg', 1, 41, 1, 0, 1429165913, '127.0.0.1', 1, 1, 0),
(49, '爱食派江苏正宗盱眙龙虾', '店长推荐 高蛋白低脂肪多种维生素及微量元素丰富，尤其富含不饱和脂肪酸，快来吃龙虾吧！', '2015/04/24/thumb_5539b46f926c2.jpg', 1, 25, 22, 0, 1429845121, '127.0.0.1', 0, 1, 0),
(50, '休闲创意沙发 布艺沙发组合', '小户型沙发家具158 黑白色 A+B+C+D 2.3米\r\n家装节 优惠卷满1000元减100元 237个城区送到家 全网独销，原创小丑设计。', '2015/04/24/thumb_5539b84e425ee.jpg', 4, 3, 1, 0, 1429846153, '127.0.0.1', 0, 1, 0),
(51, '牛皮沙发 欧式古典客厅家具', '厚皮真皮沙发组合 头层真皮实木皮艺沙发 三色可 贵妃位备注方向', '2015/04/24/thumb_5539bc757a568.jpg', 4, 3, 2, 0, 1429847158, '127.0.0.1', 0, 1, 0),
(52, '现代中式餐桌餐椅', '餐桌椅组合套装实木家具CZ-1/CY一桌四椅饭桌，做够一流，精良的感觉，真正的实木，而且细节都处理很好，给人很好的感觉，而且造型真的是美爆了。', '2015/04/24/thumb_5539be4350c1b.jpg', 4, 5, 3, 0, 1429847622, '127.0.0.1', 0, 1, 0),
