INSERT INTO bao_market_activity (id, market_id, title, details, views, closed, create_time, create_ip) VALUES
(1, 1, '商之都回馈大酬宾', '<p>活动内容： <br/>&nbsp;&nbsp;凡活动期间，在本商场购任意价值物品就有机会获得“全免惊喜”。即活动期间，每天任意抽取1个小时作为免费时段，在此时段购物的顾客即可享受所购商品金额全额返还（以现金券形式）。&nbsp;&nbsp; <br/></p><p>操作细则： <br/>&nbsp;&nbsp;活动期间，每天抽出1小时作为免费时段；具体时段由财务部内部操作，选取当日销售额最低时段进行返现；财务部须对7月每周末各时段销售情况进行统计，以便预测8月活动的大体费用； <br/>&nbsp;&nbsp;当日“免费时段”于第二日上午10点准时公布在商场一楼中庭；中奖顾客须在公布当日领券，逾期作废； <br/>&nbsp;&nbsp;在“免费时段”购物的顾客凭手工小票顾客联、电脑小票和本人有效证件到商场服务台兑换与所购商品等值的现金券（商品价格个位数不计，十位起算）；&nbsp;单张电脑小票最高返现金额不超过1000元； <br/>&nbsp;&nbsp;返现时请保持电脑小票完好。如发现有任何涂改、破损，我公司将有权取消顾客的参与资格； <br/>&nbsp;&nbsp;现金券不找零、不兑换现金，可在商场各专柜通用，有效期为8月6日至8月31日； <br/>&nbsp;&nbsp;本次活动最终解释权归商之都（东城店）公司所有。</p><p><br/></p>', 2343, 0, 1423043269, '127.0.0.1'),
(2, 2, '金鹰购物中心回馈大酬宾', '<p>凡一次性购物金额满200元，即可凭电脑小票到服务台的幸运箱内参与抽奖； <br/>&nbsp;&nbsp;抽中者即可参加由本公司组织的暑期夏令营惠州大漂流活动。 <br/>操作细则： <br/>&nbsp;&nbsp;凡一次性购满200元即可参与抽奖，抽奖时须在现场填写“姓名、身份证号码、联系电话”等基本资料，便于领奖； <br/>&nbsp;&nbsp;中奖人数按销售单数10%确定，若200元有10单，则抽出1单中奖；200元有100单，则抽出10单中奖，以此类推；&nbsp;&nbsp;&nbsp;漂流费用：每人138元 <br/>&nbsp;&nbsp;此活动可作为参考活动，根据实际情况进行调整。</p>', 165, 0, 1423044615, '127.0.0.1'),
(3, 1, '圣诞大促销', '<p>1．预定圣诞节期间包间的礼物赠送方法，参考宣传篇第3点。<br/>2．本店现有会员及时尚消费卡持有者预定包间享有更多优惠。赠送 饮料等。<br/>3．平安夜圣诞树亮灯仪式<br/>公司经理在12月24日晚10点，在一楼举行圣诞树亮灯仪式，顾客可以写下愿望放进圣诞盒。由经理封盒、保存在店内显目位置。目的是建立客户的忠诚。<br/>4．免费品尝点心。<br/>5．圣诞老人在各包间的客串活动。<br/>6．圣诞贺卡的派送<br/>收：包间号 寄：C<br/>&nbsp;突出企业“消费者至上”的理念。<br/>7．圣诞老人可应要求到包间与消费者合影。（收费服务）<br/>8．庆元旦，C再掀风暴<br/>&nbsp;新年伊始，C真情回报广大消费者。具体优惠待定。<br/>9．圣诞树熄灯仪式<br/>&nbsp;整个活动结束的高潮，共唱圣诞歌。为自己、家庭、朋友、国家祈福。<br/>10．“C2007年与您共同进步”签名仪式</p>', 234, 0, 1423044861, '127.0.0.1');
